<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Text * * @author Alex */class Textarea extends Field{        public function tableData($table)    {        $table->text($this->getName());        return $table;    }        public function render($row)    {        $value = '';        if($row)        {            $name = $this->getName();            $this->setValue($row->$name);        }                $required = '';        if($this->getRequired())           $required = '<span class="required">*</span>';                $subTitle = '';        if($this->getSubTitle())            $subTitle = '<span class="subtitle">'.$this->getSubTitle().'</span>';                return '<div class="form-group">                    '.Form::label($this->getName(), $this->getTitle())                     .$required                     .$subTitle                     .Form::textarea($this->getName(), $this->getValue(),  array('class' => 'form-control', 'id' => $this->getName(), 'placeholder' => '')).'                </div>';    }}